package Switch;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=8;
switch(a+8)
{
case 1:
	  System.out.println("Case1: Value is: "+a);
	  break;
	case 2:
	  System.out.println("Case2: Value is: "+a);
	break;
	case 3:
		System.out.println("Case3: Value is: "+a);
		break;
	
      default:
	  System.out.println("Default: Value is: "+a);
	break;
			
}
	}

}
