package lesson10;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int score=80;
switch(score)
{
case 100:
case 90:
case 80:
	System.out.println("very good");
break;
case 60:
	System.out.println("good");
break;
case 40:
	System.out.println("ok");
break;
default:
	System.out.println("ok");
	break;
}
	}

}
